import { setItem } from './_demo.js'

export default async ({ username, password }) => {
	if (password === 'abc123') {
		const user = {
			username,
			firstname: 'John Jacob',
			lastname: 'Jingleheimerschmidt',
			accountId: '0001'
		}
		await setItem('user', user)
		return user
	} else {
		return Promise.reject({ status: 400 })
	}
}
