<script>
import { createEventDispatcher } from 'svelte'

const dispatch = createEventDispatcher()
const logout = () => {
	dispatch('logout')
}

export let currentUser = {}
</script>

<p>route: app</p>

<p>Username: {currentUser.username}</p>

<p>
	<button
		class="btn btn-primary"
		on:click={logout}
	>
		Log Out
	</button>
</p>

<uiView />
