<script>
import { createEventDispatcher } from 'svelte'
import Button from 'app/element/Button.svelte'
import Card from 'app/element/Card.svelte'
import Container from 'app/element/Container.svelte'
import LabelInputText from 'app/element/LabelInputText.svelte'

let username = ''
let password = ''

const dispatch = createEventDispatcher()
const login = () => {
	dispatch('login', { username, password })
}
</script>

<Container>
	<div style="max-width: 25rem; margin: 0 auto;">
		<Card>
			<div slot="header">
				<h1 class="card-title">Log In</h1>
			</div>
			<div slot="body">
				<p>Enter your username and password to log in.</p>

				<LabelInputText>
					<span slot="label">
						Username
					</span>
					<span slot="input">
						<input
							type="text"
							placeholder="user@site.com"
							bind:value={username}
						>
					</span>
					<span slot="helptext">
						Your email address or username is fine.
					</span>
				</LabelInputText>

				<LabelInputText>
					<span slot="label">
						Password
					</span>
					<span slot="input">
						<input
							type="password"
							bind:value={password}
						>
					</span>
				</LabelInputText>

				<Button
					on:click={login}
				>
					Sign In
				</Button>
			</div>
		</Card>
	</div>
</Container>

<p>route: login</p>
